mapper.select:
		select * from board
		
		
			where kind= #{kind} and sNo = #{sNo}
		

		
			where bNo = #{bNo}
		 
		
		
			where kind = #{kind}
		
		
		
			and id = #{id} 
		 
		
		
			and ${searchType} like '%${searchContent}%'
		
		
		
			order by ref desc limit #{startrow}, #{limit}

select count(*) from board 



	where kind = #{kind} and sNo = #{sNo}	



	where kind = #{kind}	


	and ${searchType} like '%${searchContent}%'
