mapper.select:
select count(*) from member


	where ${searchType} like '%${searchContent}%'



	where regDate >= #{startDate} and regDate <= #{endDate}

order by #{searchType} desc limit #{pageNum}, #{limit}

select * from member 


	where ${searchType} like '%${searchContent}%'



	where regDate >= #{startDate} and regDate <= #{endDate}

select count(*) from member where memType = 1


	and ${searchType} like '%${searchContent}%'



	and regDate >= #{startDate} and regDate <= #{endDate}

order by #{searchType} desc limit #{pageNum}, #{limit}

select * from member where memType = 1


	and ${searchType} like '%${searchContent}%'



	and regDate >= #{startDate} and regDate <= #{endDate}

select * from Member where id in 

	#{value}

and not id = 'admin'

select * from member

	where name = #{name}


	and email = #{email}

select * from member

	where name = #{name}


	and email = #{email}


	and id = #{id}
