mapper.select:
	select host, guest, hostName, email, address, regDate, tel, accountNo, sNo, sName, srNo, srName, reNo, reDate, totPrice, count(*) 'cnt'	
	from transactionHistory where reDate > date_format(now(), '%Y-%m-01') and reDate < now()

	
		group by sNo

	select * from transactionHistory

	
		where ${searchType} like '%${searchContent}%' order by ${searchType}
	

	
		where reDate > ${startDate} and reDate < ${endDate} order by reDate
	
	
	
		where ${searchType} like '%${searchContent}%' and reDate > ${startDate} and reDate < ${endDate} order by ${searchType}
